scene.onOverlapTile(SpriteKind.Player, sprites.dungeon.collectibleInsignia, function (sprite, location) {
    info.changeScoreBy(1)
    if (info.score() == 1) {
        nivel = 2
        info.stopCountdown()
        nivel_2()
    } else if (info.score() == 2) {
        nivel = 3
        info.stopCountdown()
        nivel3()
    } else if (info.score() == 3) {
        info.stopCountdown()
        game.over(true, effects.confetti)
    }
})
